def classification(oldSample):
   sample = samplePreprocessor(oldSample, joblib.load(SUPPORT_FN))
   X_new = np.array([sample])

   # Get a prediction
   model = joblib.load(LOGISTIC_REGRESSION_MODEL_FN)
   pred = model.predict(X_new)
   pred_pro = model.predict_proba(X_new)
   # The model returns an array of predictions - one for each set of features submitted
   # In our case, we only submitted one patient, so our prediction is the first one in the resulting array.
   repport = f'''Predicted file is {"<span style='color:green' >Not a Spyware</span>" if pred[0] == -1 else "<span style='color:red' >a Spyware</span>" } 
      <br> with probapilty : {pred_pro[0][0]*100:.2f}% Not a spayware and 
      {pred_pro[0][1]*100:.2f}% is a Spayware'''
   return render_template('index.html', repport=repport)

def samplePreprocessor(oldSample, support):
   sample = []
   for i in range(len(support)):
      if support[i]:
         sample.append(oldSample[i])
   return sample

